import { Chart } from "react-google-charts";
import _ from "loadsh";
export default function fundo({ children: fundo = null }) {
  const chave = "3176856a-82cf-4ce9-8803-c65107c7ad5e";
  function relatorio(id) {
    let rel = fundo.relatorio.filter((item) => {
      return item.investmentId === id;
    });
    const result = _.orderBy(rel, ["month", "year"], ["asc", "desc"]);
    return [
      ["month", "year", "rendimento"],
      ..._.map(result, (value) => {
        return [value.month, value.year, value.value];
      }),
    ];
  }
  let relatorio2 = relatorio(chave);

  console.log(relatorio2);

  const data = [
    ["Year", "Sales", "Expenses", "Profit"],
    ["2014", 1000, 400, 200],
    ["2015", 1170, 460, 250],
    ["2016", 660, 1120, 300],
    ["2017", 1030, 540, 350],
  ];
  const options = {
    chart: {
      title: "Company Performance",
      subtitle: "Sales, Expenses, and Profit: 2014-2017",
    },
  };
  if (!fundo) {
    return <div>Sem Fundos</div>;
  }
  return (
    <div fundoid={fundo.id} className="border p-1 border-gray-light m-2">
      {fundo.description}
      <Chart
        chartType="Bar"
        width="100%"
        height="400px"
        data={relatorio(fundo.id)}
        options={options}
      />
    </div>
  );
}
