import Header from "../components/Header";
import { useState } from "react";
import Main from "../components/Main";
import TextInput from "../components/TextInput";
import DateInput from "../components/DateInput";
import Fundos from "../components/Fundos";
import { data } from "../data";
export default function App() {
  console.log("Teste no console do navegador");
  const [name, setName] = useState("");
  function handleNameChange(newName) {
    setName(newName);
  }

  const allfundos = data[0].investments;
  const allreports = data[0].reports;
  return (
    <>
      <Header>React Investiments</Header>
      <Main>
        <div>
          <h2 className="text-left">Relatório de Investimentos</h2>
        </div>
        <TextInput
          id="textInput"
          labelDescription="Digite o fundo de ações desejado:"
          inputValue={name}
          onInputChange={handleNameChange}
        />
        <Fundos>{allfundos}</Fundos>
        <p>
          Seu nome é {name}, com {name.length} caracteres e você possui 38 anos.
        </p>
      </Main>
    </>
  );
}
