import { Chart } from "react-google-charts";
import _ from "loadsh";
export default function fundo({ children: fundo = null }) {
  const chave = "3176856a-82cf-4ce9-8803-c65107c7ad5e";
  function relatorio(id) {
    let rel = fundo.relatorio.filter((item) => {
      return item.investmentId === id;
    });
    const result = _.orderBy(rel, ["month", "year"], ["asc", "desc"]);
    const data = [
      ["mês", "valor"],
      ..._.map(result, (value) => {
        return [value.month, value.value];
      }),
    ];
    return data;
  }

  let relatorio2 = relatorio(chave);

  function performance(report) {
    let performance = _.map(report, (item) => {
      return item[1];
    });
    let valor = _.drop(performance);
    let rend = [0];
    for (var i = 1; i < 12; i++) {
      rend.push([((valor[i] - valor[i - 1]) / valor[i - 1]) * 100]);
    }
    const rends = [["rendimento"], [...rend]];
    return rends;
  }

  console.log(performance(relatorio2));

  const options = {
    chart: {
      title: "Performance do Fundo",
      subtitle: "Valor x Mês em 2020",
    },
  };
  if (!fundo) {
    return <div>Sem Fundos</div>;
  }
  return (
    <div fundoid={fundo.id} className="border p-1 border-gray-light m-2">
      {fundo.description}
      <Chart
        chartType="Bar"
        width="100%"
        height="400px"
        data={relatorio(fundo.id)}
        options={options}
      />
      <Chart
        chartType="Line"
        width="100%"
        height="400px"
        data={performance(relatorio(fundo.id))}
        options={options}
      />
    </div>
  );
}
